import React, { useState, useEffect } from "react";
import { useParams, useHistory,Link } from "react-router-dom";
import axios from "axios";

const EditMovieForm = (props) => {
  const {id} = useParams();
  const { push } = useHistory();

  const { movies,setMovies } = props;
  const [movie, setMovie] = useState({
    title: "",
    director: "",
    genre: "",
    metascore: 0,
    description: "",
  });

  useEffect(()=> {
    axios
    .get("http://localhost:9000/api/movies/${id}")
    .then((res)=> {
      setMovie(res.data);
    })
    .catch((err)=> {
      console.log(err);
    });
  }, [id]);

  const handleChange = (e) => {
    setMovie({
      ...movie,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .put(`http://localhost:9000/api/movies/${id}`, movie)
      .then((res) => {
        const updatedMovies = movies.map((m) => {
          if (m.id === id) {
            return {
              ...m,
              title: movie.title,
              director: movie.director,
              genre: movie.genre,
              metascore: movie.metascore,
              description: movie.description,
            };
          }
          return m;
         });
        setMovies(updatedMovies);
        const updatedFavoriteMovies = favoriteMovies.map((favMovie) => {
          if (favMovie.id === id) {
            return {
              ...favMovie,
              title: movie.title,
            };
          }
          return favMovie;
        });
        setFavoriteMovies(updatedFavoriteMovies);

        push(`/movies/${id}`);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const { title, director, genre, metascore, description } = movie;

  return (
    <div className="bg-white rounded-md shadow flex-1">
      <form onSubmit={handleSubmit}>
        <div className="p-5 pb-3 border-b border-zinc-200">
          <h4 className="text-xl font-bold">Düzenleniyor <strong>{movie.title}</strong></h4>
        </div>

        <div className="px-5 py-3">
          <div className="py-2">
            <label className="block pb-1 text-lg">Title</label>
            <input
              value={title}
              onChange={handleChange}
              name="title"
              type="text"
            />
          </div>
          <div className="py-2">
            <label className="block pb-1 text-lg">Director</label>
            <input
              value={director}
              onChange={handleChange}
              name="director"
              type="text"
            />
          </div>
          <div className="py-2">
            <label className="block pb-1 text-lg">Genre</label>
            <input
              value={genre}
              onChange={handleChange}
              name="genre"
              type="text"
            />
          </div>
          <div className="py-2">
            <label className="block pb-1 text-lg">Metascore</label>
            <input
              value={metascore}
              onChange={handleChange}
              name="metascore"
              type="number"
            />
          </div>
          <div className="py-2">
            <label className="block pb-1 text-lg">Description</label>
            <textarea
              value={description}
              onChange={handleChange}
              name="description"
            ></textarea>
          </div>
        </div>

        <div className="px-5 py-4 border-t border-zinc-200 flex justify-end gap-2">
          <Link to={`/movies/${id}`} className="myButton bg-zinc-500">
            Vazgeç
          </Link>
          <button
            type="submit"
            className="myButton bg-green-700 hover:bg-green-600"
          >
            Ekle
          </button>
        </div>
      </form>
    </div>
  );
};

export default EditMovieForm;
