import React, { useState } from "react";
import { Button, Form, FormGroup, Label, Input } from "reactstrap";

const AddMovieForm = () => {
  const [movie, setMovie] = useState({
    title: "",
    director: "",
    genre: "",
    metascore: "",
    description: "",
  });

  const handleChange = (e) => {
    setMovie({ ...movie, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Yeni filmi göndermek için uygun API çağrısını burada yapabilirsiniz
    console.log(movie);
    setMovie({
      title: "",
      director: "",
      genre: "",
      metascore: "",
      description: "",
    });
  };

  return (
    <div>
      <h3 className="mb-4">Film Ekle</h3>
      <Form onSubmit={handleSubmit}>
        <FormGroup>
          <Label for="title">Film Adı</Label>
          <Input
            type="text"
            name="title"
            id="title"
            value={movie.title}
            onChange={handleChange}
            required
          />
        </FormGroup>

        <FormGroup>
          <Label for="director">Yönetmen</Label>
          <Input
            type="text"
            name="director"
            id="director"
            value={movie.director}
            onChange={handleChange}
            required
          />
        </FormGroup>

        <FormGroup>
          <Label for="genre">Tür</Label>
          <Input
            type="text"
            name="genre"
            id="genre"
            value={movie.genre}
            onChange={handleChange}
            required
          />
        </FormGroup>

        <FormGroup>
          <Label for="metascore">Metascore</Label>
          <Input
            type="number"
            name="metascore"
            id="metascore"
            value={movie.metascore}
            onChange={handleChange}
            required
          />
        </FormGroup>

        <FormGroup>
          <Label for="description">Açıklama</Label>
          <Input
            type="textarea"
            name="description"
            id="description"
            value={movie.description}
            onChange={handleChange}
            required
          />
        </FormGroup>

        <Button color="primary" type="submit">
          Film Ekle
        </Button>
      </Form>
    </div>
  );
};

export default AddMovieForm;
